mapper.select:
        select * from tb_user 
		
			
				id=#{id}
			
			
				and name like '%${name}%' 
			
			
				and departId =#{departId}
					
			
				and loginName=#{loginName}
			
			
				and password=#{password}
			
		
		 
				limit #{pageStart},#{pageSize}

    	select count(*) from tb_user 
    		
			
				id=#{id}
			
			
				and name like '%${name}%' 
			
			
				and departId =#{departId}
					
			
				and loginName=#{loginName}
			
			
				and password=#{password}
